import React from 'react';
import { BrowserRouter as Router, Route, NavLink } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import Alert from 'react-bootstrap/Alert'
import './App.css';
import NavBar from './components/NavBar'
import Home from './components/Home'
import Login from './components/Login'
import Result from './components/Result'

import TestContainer from './containers/TestContainer'

// convert to class based component and add state for user

export default class App extends React.Component {

  state = {
    username: "",
    name: "",
    loggedIn: false,
    results: []
  }

  handleLogin = (userData) => {
    this.setState({
      user: userData,
      loggedIn: true
    })
    debugger
  }

  render() {

    return ( 
      <div>

        <Router>
          <div>
            <NavBar loggedIn={this.state.user.loggedIn} name={this.state.user.name} />
            <Route exact path="/" component={Home} />
            <Route exact path="/login" render={routerProps => <Login {...routerProps} returnLogin={this.handleLogin} />} />
            <Route exact path="/test" user={this.state.user} component={TestContainer} />
            <Route exact path="/results" user={this.state.user} component={Result} />
          </div>

        </Router>
      </div>
    );

  }
}
