import React from 'react';
import { BrowserRouter as Router, Route, NavLink, Redirect } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import Alert from 'react-bootstrap/Alert'
import './App.css';
import NavBar from './components/NavBar'
import Home from './components/Home'
import Login from './components/Login'
import Result from './components/Result'
import TestContainer from './containers/TestContainer'

// convert to class based component and add state for user

export default class App extends React.Component {

  state = {
    username: "",
    name: "",
    loggedIn: false,
    results: []
  }

  handleLogin = (userData) => {
    this.setState({
      username: userData.username,
      name: userData.name,
      loggedIn: true,
      results: userData.results
    })
  //  {this.TestContainer}
  }

  render() {
    if (this.state.loggedIn) {
      console.log('Redirecting to Test!')
      return <Redirect to="/test" push={true} />
    }

    return ( 
      <div>

        <Router>
          <div>
            <NavBar loggedIn={this.state.loggedIn} name={this.state.name} />
            <Route exact path="/">
              {this.state.loggedIn ? <Redirect to="/test" /> : <Home />}
            </Route>
            <Route exact path="/login" render={routerProps => <Login {...routerProps} returnLogin={this.handleLogin} />} />
            <Route exact path="/test" user={this.state} component={TestContainer} />
            <Route exact path="/results" user={this.state} component={Result} />
          </div>

        </Router>
      </div>
    );

  }
}
